package app;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int x = 5;
		
		System.out.println("Bom Dia!");
		
		if (x < 0) {                           //Reparar na endentação,indicando que esse trecho do código
		    System.out.println("Boa Tarde!");   //esta dentro desta estrutura condicional => ENDENTAÇÃO <= // 
		}                                        //Para facilitar a legibilidade do seu código//
		
		System.out.println("Boa Noite!");
		
		
		//Sintaxe estrutura condicional simples//
		
		// => Regra = V : executa o bloco de comandos
		           // F : pula o bloco de comandos 

	}

}
